import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { getNameRecipe } from "../actions";
import {Error} from '../components/Error.jsx'


import "../styles/SearchBar.css";

export default function SearchBar() {
  const dispatch = useDispatch();

  const [name, setName] = useState("");
  const [page404, setPage404] = useState(false)
  const [error, setError] =  useState(null)
 


  function handleSubmit(e) {
    e.preventDefault();
    if (!name) {
      return alert("It's required to enter a name in order to make a search")
    } else {
      try{
    dispatch(getNameRecipe(name));   
    setName("");
    document.querySelector("#inputsearch").value = "";

      } catch(error) {console.log("No pude encontrar la receta");
      setError('Error al cargar las recetas') 

      }
    }
  

  function handleInputChange(e){
    e.preventDefault();
    setName(e.target.value);
    
    
  }



  return (
    <><div className="searchBar">
      <input
        id="inputsearch"
        className="input"
        type="text"
        value={name}
        placeholder="Search recipe..."
        onChange={(e) => handleInputChange(e)}
        autoComplete='false'

        
      />
      
      <button type="submit" onClick={(e) => handleSubmit(e)} className="btn">
        <span class="material-icons">search</span>
       
      </button>
    </div>

    {error && <Error error={error} />}
    </>
  );
}
}
