import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import "./Paginate.css";

//action 
import {setPagIndexes} from "../../actions/index";



export default function Paginate({paginate, handleNext, handleSupNext, handleSupPrev, handlePrev, maxPageDisplay, minPageDisplay}) {

const dispatch = useDispatch();
const allRecipes = useSelector(state => state.recipes.length);

// 

const [currentPage, setCurrentPage] = useState(1);
const recipesPerPage= 9;

const indexOfLastRecipe = currentPage * recipesPerPage;   
const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;





  // actualizo el state del store con index last e index first
  useEffect(
    () => dispatch(setPagIndexes(indexOfLastRecipe, indexOfFirstRecipe)),
    [indexOfLastRecipe, indexOfFirstRecipe, dispatch]
  );

  //* Reset to first page when recipes changes

  //* Calculate number of posssible pages
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {
    pageNumbers.push(i);
  }

  


  //* functions to pass to prev/next page


  

  

  return (
   /*<nav className="btnPag">
      {pageNumbers &&
        pageNumbers.map((number) => (
        <button key={number} onClick={() => paginate(number)}>
            {number}
          </button>
        ))}
    </nav>
  );*/

  <nav className="btnPag">
      {pageNumbers.length === 0 ? (
        <p></p>
      ) : (
        <ul>
          <li onClick={()=> handleSupPrev(currentPage)}>{"<<"}</li>
          <li onClick={()=> handlePrev(currentPage)}>{"<"}</li>
          {pageNumbers?.map(number => {
            if (number <= maxPageDisplay && number >= minPageDisplay) {
              return (
                <li
                  key={number}
                  id={number}
                  className={currentPage === number ? "active" : null}
                  onClick={() => paginate(number)}
                >
                  {number}
                </li>
              );
            } else {
              return null;
            }
          })}
          <li onClick={()=> handleNext(currentPage)}>{">"}</li>
          <li onClick={()=> handleSupNext(currentPage)}>{">>"}</li>
        </ul>
      )}
    </nav>
  );
}
