import React from "react";

import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDetail, clearDetails, deleteRecipe } from "../../actions";
import { useHistory, useParams  } from "react-router";
import { Link } from "react-router-dom";
import "./Detail.css";
import Loading from "../loading/Loading";
import DeleteConfirm from "../modal/DeleteConfirm"

//utils
import DeleteRecipes from "../../utils/deleteRecipes.js"





export default function Detail(props) {
  console.log(props);
  const dispatch = useDispatch();
  const [/*cambio*/, setCambio] = useState(false);
  const detail = useSelector((state) => state.detail);
  const [popUp, setPopUp] = useState(false);
  const history = useHistory();
  const [recipe, setRecipe] = useState();
  const { source, id } = useParams();

  //const navigate = useNavigate();

  useEffect(() => {
    dispatch(getDetail(props.match.params.id));
    setCambio(true);
    return () => {
      dispatch(clearDetails());
  }
    
  }, [props.match.params.id, dispatch]);

 
  const handleDelete = () => {
    dispatch(deleteRecipe(id));
    //navigate("/home");
  };
 


  //delete
  function handleDeleteClick() {
    setPopUp(() => true);
  }
  
  function aceptPopUp() {
    console.log('jaja')
    setPopUp(false);
  }
  
  

  //cancel? ok
  function cancelPopUp() {
    console.log("iwi")
    DeleteRecipes(props.match.params.id)
      .then(() => {
        history.push(`/home`);
      })
      .catch(error => console.error(error));
    dispatch(deleteRecipe(props.match.params.id));
    setPopUp(false);
  }



  return (
    <div className="detail">
      <Link to="/home">
        <button>Back to Home</button>
      </Link>
      {detail.length ? 
      (<div>
          <h1> "{detail[0].title}"</h1>
          
          <img src={detail[0].image 
          ?(detail[0].image) 
          :(<img src="https://shorturl.ae/eEB8K" alt="img plate"/>)} alt="img recipe"/>

          <div className="h3-2">

            {detail[0].createdDb 
            ?(<h2>Type of Diets: {detail[0].diets.map((d) => d.name).join(", ")}</h2>) 
            :(<h2>Type of Diets:
                {detail[0].vegetarian === true
                  ? " " + detail[0].diets.join(", ") + ", vegetarian"
                  : " " + detail[0].diets.join(", ")}
              </h2>)}

            <h2>
              {detail[0].createdDb
                ? null
                : "Dish types: " + detail[0].dishTypes.join(", ")}
            </h2>

            <button onClick={handleDelete}>
                    
                  </button>

          </div>

          {popUp && (
        <DeleteConfirm
          text="Are you sure you want to delete this recipe??"
          aceptText="Delete"
          cancelText="Cancel"
          aceptPopUp={aceptPopUp}
          cancelPopUp={cancelPopUp}
        /> )}



          <div className="details">
            {detail[0].aggregateLikes !== 0 
            ?(<h2>Score: {detail[0].aggregateLikes}</h2>) 
            :(<h2>Score: - </h2>)}


            {detail[0].healthScore !== 0 
            ?(<h2>Health Score: {detail[0].healthScore}</h2>) 
            :(<h2>Health Score: - </h2>)}

            <h2>Summary:</h2>

            <p>{detail[0].summary.replace(/<[^>]*>?/g, "")}</p>
            {detail[0].analyzedInstructions 
            ?(<h2>Step by step instructions: </h2>) 
            :(<h2>Step by step instructions: - </h2>)}


            {detail[0].analyzedInstructions.length > 0 
            ?(<ul>{detail[0].createdDb 
              ?(<li>{detail[0].analyzedInstructions}</li>) 

              :(detail[0].analyzedInstructions[0].steps.map((p) => 

              (<li key={p.number}>{p.step}</li>)))}
              </ul>) 

              :(<p>Vegetarian</p>)}

        </div>

        </div>) 

        : (<Loading />)}

        </div>
  );
}
