import React from "react";
import { useState, useEffect } from "react";
import "./Paginate.css";

/*export default function Paginate({ recipesPerPage, allRecipes, paginate }) {
  const pageNumbers = [];

  for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {
    pageNumbers.push(i);
  }
  return (
   <nav className="btnPag">
      {pageNumbers &&
        pageNumbers.map((number) => (
        <button key={number} onClick={() => paginate(number)}>
            {number}
          </button>
        ))}
    </nav>
  );
}*/


export default function Paginate({allRecipes, getRecipes}){
  function getRecipesFunction(){
    getRecipes()
  }
  useEffect(()=> {
    getRecipesFunction()
  }, [])

  const [render, setRender] =  useState(allRecipes)

const [currentPage, setCurrentPage] = useState(1);        // aca seteo la pagina inicial en 1
const [recipesPerPage, /*setRecipesPerPage*/] = useState(9);  // le pido paginar 9  cards en cada page

const [recipesPerPageLimit] = useState(10)
  const [maxRecipesPerPageLimit, setmaxRecipesPerPageLimit] = useState(10);
  const [minRecipesPerPageLimit , setminRecipesPerPageLimit] = useState(0);


  const paginate = (event) => {   // esto setea el paginado
    setCurrentPage(Number(event.target.id));
    };

    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {
      pageNumbers.push(i);
    }

    
  const indexOfLastRecipe = currentPage * recipesPerPage;   
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;

  const currentRecipes = allRecipes.slice(
    indexOfFirstRecipe,
    indexOfLastRecipe
  );

  const renderPageNumers = pageNumbers.map((number) =>{
    if (number < maxRecipesPerPageLimit + 1 && number > minRecipesPerPageLimit){
      return (
        <li key={number}
        id= {number}
        onClick={paginate}
        className={currentPage === number ? 'active' : null}
        >
          {number}
        </li>
      );
    } else {
      return null;
    }
 
  });
  
  const handleNextbtn = () => {
    setCurrentPage(currentPage +1);
    if (currentPage + 1 > maxRecipesPerPageLimit) {
      setmaxRecipesPerPageLimit(maxRecipesPerPageLimit + recipesPerPageLimit)
      setminRecipesPerPageLimit(minRecipesPerPageLimit + recipesPerPageLimit)
    }
  };

  const handlePrevbtn = () =>{

    if ((currentPage - 1 ) % recipesPerPageLimit === 0) {
      setmaxRecipesPerPageLimit(maxRecipesPerPageLimit - recipesPerPageLimit);
      setminRecipesPerPageLimit(minRecipesPerPageLimit - recipesPerPageLimit);
      
    }
 }



 let pageIncrementBtn = null;
 if (pageNumbers.lenght >  maxRecipesPerPageLimit){
   pageIncrementBtn = <li onClick={handleNextbtn}>&hellip </li> 
 }

 let pageDecrementBtn = null;
 if (minRecipesPerPageLimit >= 1){
   pageDecrementBtn = <li onClick={handlePrevbtn}>&hellip</li>
 }


 return(
  <div className="paginate">
  <ul>
    <li>
      <button 
      onClick={handlePrevbtn}
      disabled={currentPage === pageNumbers[0]? true : false}
      >
        Prev
      </button>
    </li>

    {pageDecrementBtn}
    {renderPageNumers}
    {pageIncrementBtn}


    <li>
      <button
      onClick={handleNextbtn}
      disabled={currentPage === pageNumbers[pageNumbers.length - 1] ? true : false}>
      Next
      </button>
    </li>
  </ul>
</div>
 )

 

}
