import React from "react";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  getRecipes,
  filterByDiet,
  getTypesOfDiet,
  orderByName,
  orderByScoreLikes,
  clearError,
 
} from "../../actions";
import { Link } from "react-router-dom";
import Card from "../Card/Card";
//import Paginate from "../Paginate/Paginate";
import SearchBar from "../SearchBar/SearchBar";
import LoadingHome from "../loading/LoadingHome"
import Modal from "../modal/Modal";


import "./Home.css";

const [currentPage, setCurrentPage] = useState(1);  

const currentRecipes = allRecipes.slice(
  indexOfFirstRecipe,
  indexOfLastRecipe
);


function PaginateComponent({allRecipes, getRecipes}){
  function getRecipesFunction(){
    getRecipes()
  }
  useEffect(()=> {
    getRecipesFunction()
  }, [])

  const [render, setRender] =  useState(recipes)

const [currentPage, setCurrentPage] = useState(1);        // aca seteo la pagina inicial en 1
const [recipesPerPage, /*setRecipesPerPage*/] = useState(9);  // le pido paginar 9  cards en cada page

const [recipesPerPageLimit] = useState(10)
  const [maxRecipesPerPageLimit, setmaxRecipesPerPageLimit] = useState(10);
  const [minRecipesPerPageLimit , setminRecipesPerPageLimit] = useState(0);


  const paginate = (event) => {   // esto setea el paginado
    setCurrentPage(Number(event.target.id));
    };

    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {
      pageNumbers.push(i);
    }

    
  const indexOfLastRecipe = currentPage * recipesPerPage;   
  const indexOfFirstRecipe = indexOfLastRecipe - recipesPerPage;

  const currentRecipes = allRecipes.slice(
    indexOfFirstRecipe,
    indexOfLastRecipe
  );

  const renderPageNumers = pageNumbers.map((number) =>{
    if (number < maxRecipesPerPageLimit + 1 && number > minRecipesPerPageLimit){
      return (
        <li key={number}
        id= {number}
        onClick={paginate}
        className={currentPage === number ? 'active' : null}
        >
          {number}
        </li>
      );
    } else {
      return null;
    }
 
  });
  
  const handleNextbtn = () => {
    setCurrentPage(currentPage +1);
    if (currentPage + 1 > maxRecipesPerPageLimit) {
      setmaxRecipesPerPageLimit(maxRecipesPerPageLimit + recipesPerPageLimit)
      setminRecipesPerPageLimit(minRecipesPerPageLimit + recipesPerPageLimit)
    }
  };

  const handlePrevbtn = () =>{

    if ((currentPage - 1 ) % recipesPerPageLimit === 0) {
      setmaxRecipesPerPageLimit(maxRecipesPerPageLimit - recipesPerPageLimit);
      setminRecipesPerPageLimit(minRecipesPerPageLimit - recipesPerPageLimit);
      
    }
 }



 let pageIncrementBtn = null;
 if (pageNumbers.lenght >  maxRecipesPerPageLimit){
   pageIncrementBtn = <li onClick={handleNextbtn}>&hellip </li> 
 }

 let pageDecrementBtn = null;
 if (minRecipesPerPageLimit >= 1){
   pageDecrementBtn = <li onClick={handlePrevbtn}>&hellip</li>
 }


 return(
  <div className="paginate">
  <ul>
    <li>
      <button 
      onClick={handlePrevbtn}
      disabled={currentPage === pageNumbers[0]? true : false}
      >
        Prev
      </button>
    </li>

    {pageDecrementBtn}
    {renderPageNumers}
    {pageIncrementBtn}


    <li>
      <button
      onClick={handleNextbtn}
      disabled={currentPage === pageNumbers[pageNumbers.length - 1] ? true : false}>
      Next
      </button>
    </li>
  </ul>
</div>
 )

 

}

//paginate

 


export default function Home() {
  const [, /*refreshState*/ setRefreshState] = useState(false);
  const dispatch = useDispatch();
  const allRecipes = useSelector((state) => state.recipes);
  const diets = useSelector((state) => state.diets);
  const error = useSelector(state => state.error);



  //Paginado:




  
  // ver aca 
 


 const [/*orderName*/, setOrderName] = useState("");
 const [/*orderLike*/, setOrderLike] = useState("");
 const [/*cambio*/, setCambio] = useState(false);








  useEffect(() => {
    dispatch(getRecipes());  // trae todas las recetas
    setCambio(true);
    
   
    
    
  }, [dispatch]);
  

  useEffect(() => {
    dispatch(getTypesOfDiet());  // trae los tipos  de  dieta
  }, [dispatch]);

  const clearErrors = () => { //manejo de errores
    dispatch(clearError());
}

  function handleClick(e) {    // con este handle click me traigo todas las recetas
    e.preventDefault();
    dispatch(getRecipes());
    
  }


  function handleSelectTypeOfDiet(e) {         
    dispatch(filterByDiet(e.target.value));
    
    setCurrentPage(1)

    setRefreshState((prevState) => !prevState);
    
  }

  function handleSelectByName(e) {
    e.preventDefault();
    dispatch(orderByName(e.target.value));
    setCurrentPage(1);
    setOrderName("Order" + e.target.value);
  }


  function handleSelectByScore(e) {
    e.preventDefault();
    dispatch(orderByScoreLikes(e.target.value));
    setCurrentPage(1);
    setOrderLike("Order" + e.target.value);
  }

  
  

  return (
    <div className="home">
      <h1>pick one for me!!!<img src="https://i.ibb.co/WFBhFLb/Harsh-Stickers1.png" alt="Harsh-Stickers1" border="0" /></h1>
      <SearchBar />

      {error && <Modal show={true} setShow={clearErrors} message={"Found no results"} />}
      
      <Link to="/recipe" className="linkCreate">
        <button className="btnCreate">Create your own recipe</button>
      </Link>
      <div className="showAll">
        <button
          onClick={(e) => {
            handleClick(e);
          }}
        >
          Show all recipes
          

          
        </button>
      </div>

   
      <div className="select">


        <span className="span">Order by Recipe Name</span>
        <select onChange={(n) => handleSelectByName(n)}>
          <option value="default">All</option>
          <option value="A-Z">A-Z</option>
          <option value="Z-A">Z-A</option>
        </select>


        <span className="span">Order by Score</span>
        <select onChange={(s) => handleSelectByScore(s)}>
          <option value="All">All</option>
          <option value="Asc">Highest Score</option>
          <option value="Desc">Lowest Score</option>
        </select>


        <span className="span">Filter by Type of diet</span>
        <select onChange={(e) => handleSelectTypeOfDiet(e)}>
          <option value="default">All Diets</option>

          {diets.map((d) => (
            <option value={d.name} key={d.id}>
              {d.name}
            </option>
          ))}
        </select>
        </div>



       

      


      
      <div className="cards">
      
       
      {allRecipes.length>0? currentRecipes?.map((c) => {
         
        return  (
          <div key={c.id}>
             
            <Link to={"/home/" + c.id} className="linkCard">
             
              <Card
               
                title={c.title}


                image={
                  c.image 
                  ?(c.image) 
                  :(<img src="https://shorturl.ae/eEB8K" alt="Img not provided"/>)                  
                 }   


                diets={
                  c.createdDb
                    ? c.diets.map((d) => ( <p key={d.name} className="dietsMap">{d.name}</p>))
                    : c.diets.map((d) => ( <p key={d} className="dietsMap">{d}</p>))
                     }

                vegetarian={
                  c.vegetarian === true 
                  ? (<p className="dietsMap">vegetarian</p>) 
                  :(<p></p>)
                  }


                score={c.aggregateLikes}


              />
              
            </Link>
            
          </div>)})    : <LoadingHome/>}  
          
        </div> 
      
      








    </div>
  );
}
