import React, { useState, useEffect } from "react";
import { useHistory, useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

//? COMPONENTS
import ConfirmPopUp from "../modal/confirmPopUp";

//? ACTIONS
import { getDiets, getNameRecipe } from "../../actions/index";

//? SERVICES
//import axiosRecipeInfo from "../services/getRecipeInfo";
//import axiosIngredients from "../services/getIngredients";
import putRecipes from "../../utils/putRecipe";
//import validateForm from "../services/validateForm";

//? STYLES
/*import { Container, Button, Title, Image, Time, Servings, Score } from "./styles/AddRecipeSC";
import { HealthScore, Diets, Summary, Ingredients, Procedure } from "./styles/AddRecipeSC";
import { BiTimeFive } from "react-icons/bi";
import { BsStar, BsFillPeopleFill } from "react-icons/bs";
import { GiHealthNormal, GiCheckMark } from "react-icons/gi";*/



function validate(input) {
    let errors = {};
    
    input.title
      ? (errors.title = "")
      : (errors.title = "You must name the recipe");
    input.summary
      ? (errors.summary = "")
      : (errors.summary = "You must provide a summary");
    input.diets.length < 1
      ? (errors.diets = "Choose at least one diet")
      : (errors.diets = "");
    if (!input.image.includes("https://") && !input.image.includes("http://")) {
      errors.image = "This isn't a valid image address";
    } else {
      errors.image = "";
    }
    return errors;
  }



export default function UpdateRecipe() {
  const dispatch = useDispatch();
  const diets = useSelector(state => state.diets);
  //const [ingredients, setIngredients] = useState([]);
  const history = useHistory();
  const { id } = useParams();

  const [input, setInput] = useState({
    title: "",
        summary: "",
        aggregateLikes: 0,
        healthScore: 0,
        analyzedInstructions: "",
        image: "",
        diets: [],
  });

  //* Preload diets in store if empty
  /*useEffect(() => {
    if (diets.length === 0) {
      dispatch(getDiets(dispatch));
    }
  }, [dispatch, diets.length]);*/
  
  useEffect(() => {
    dispatch(getDiets());
  }, [dispatch]);

  function handleDelete(e, d) {
    e.preventDefault();
    setInput({
      ...input,
      diets: input.diets.filter((diet) => diet !== d),
    });
  }

  

  //* Axios data on mount
  /*useEffect(() => {
    axiosRecipeInfo("db", id)
      .then(res => {
        res.data.Diets = res.data.Diets.map(diet => {
          for (let i = 0; i < diets.length; i++) {
            if (diets[i].name === diet) {
              return diets[i].id.toString();
            }
          }
          return 0;
        });
        res.data.Ingredients = res.data.Ingredients.map(ingredient => {
          for (let i = 0; i < ingredients.length; i++) {
            if (ingredients[i].name === ingredient.name) {
              return { id: ingredients[i].id.toString(), amount: ingredient.amount, unit: ingredient.unit };
            }
          }
          return 0;
        });
        setInput(res.data);
      })
      .catch(err => console.error(err));
  }, [id, diets, ingredients]);*/

  function handleSelectDiet(e) {
   
    setErrors(
      validate({
        ...input,
        diets: [...input.diets, e.target.value],
      })
    );
    if(!input.diets.includes(e.target.value)){
    setInput((input) => ({
