import React from "react";
import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import "./Paginate.css";

//action 
import { setPagIndexes } from "../../actions";



export default function Paginate() {

const dispatch = useDispatch();
const allRecipes = useSelector(state => state.recipes.length);
const [currentPage, setCurrentPage] = useState(1);
const recipesPerPage = 9;
const indexLastRecipe = currentPage * recipesPerPage;
const indexFirstRecipe = indexLastRecipe - recipesPerPage;
const pagesDisplayLimit = 5;
const [maxPageDisplay, setMaxPageDisplay] = useState(5); 
const [minPageDisplay, setMinPageDisplay] = useState(1);

  // actualizo el state del store con index last e index first
  useEffect(
    () => dispatch(setPagIndexes(indexLastRecipe, indexFirstRecipe)),
    [indexLastRecipe, indexFirstRecipe, dispatch]
  );

  //* Reset to first page when recipes changes
  useEffect(() => {
    setCurrentPage(1);
    setMaxPageDisplay(5);
    setMinPageDisplay(1);
  }, [allRecipes]);

  //* Calculate number of posssible pages
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(allRecipes / recipesPerPage); i++) {
    pageNumbers.push(i);
  }

  //* paso de pagina con click
  const handleClick = e => {
    setCurrentPage(Number(e.target.id));
  };

  //* functions to pass to prev/next page
  const handleSupPrev = () => {
    if (currentPage !== 1) {
      setCurrentPage(1);
      setMaxPageDisplay(5);
      setMinPageDisplay(1);
    }
  };

  const handlePrev = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
      if (currentPage - 1 < minPageDisplay) {
        setMaxPageDisplay(
          maxPageDisplay - pagesDisplayLimit < 5 ? 5 : maxPageDisplay - pagesDisplayLimit
        );
        setMinPageDisplay(
          minPageDisplay - pagesDisplayLimit <= 0 ? 1 : minPageDisplay - pagesDisplayLimit
        );
      }
    }
  };
  

  const handleNext = () => {
    if (currentPage !== pageNumbers.length) {
      setCurrentPage(currentPage + 1);
      if (currentPage + 1 > maxPageDisplay) {
        setMaxPageDisplay(maxPageDisplay + pagesDisplayLimit);
        setMinPageDisplay(minPageDisplay + pagesDisplayLimit);
      }
    }
  };
  const handleSupNext = () => {
    const lastPage = pageNumbers.length;
    if (currentPage !== lastPage) {
      setCurrentPage(lastPage);
      setMaxPageDisplay(lastPage);
      setMinPageDisplay(lastPage - pagesDisplayLimit + 1);
    }
  };

  

  return (
   /*<nav className="btnPag">
      {pageNumbers &&
        pageNumbers.map((number) => (
        <button key={number} onClick={() => paginate(number)}>
            {number}
          </button>
        ))}
    </nav>
  );*/

  <nav className="btnPag">
      {pageNumbers.length === 0 ? (
        <p></p>
      ) : (
        <div>
          <button onClick={handleSupPrev}>{"<<"}</button>
          <button onClick={handlePrev}>{"<"}</button>
          {pageNumbers?.map(number => {
            if (number <= maxPageDisplay && number >= minPageDisplay) {
              return (
                <button
                  key={number}
                  id={number}
                  className={currentPage === number ? "active" : null}
                  onClick={handleClick}
                >
                  {number}
                </button>
              );
            } else {
              return null;
            }
          })}
          <button onClick={handleNext}>{">"}</button>
          <button onClick={handleSupNext}>{">>"}</button>
        </div>
      )}
    </nav>
  );
}
