import React, { useState, useEffect } from "react";
import { Link, useParams } from "react-router-dom";
import { postRecipe, getDiets, updateRecipe, getDetail  } from "../../actions";
import { useDispatch, useSelector } from "react-redux";
import "./RecipeCreate.css";

function validate(input) {
  let errors = {};
  
  input.title
    ? (errors.title = "")
    : (errors.title = "You must name the recipe");
  input.summary
    ? (errors.summary = "")
    : (errors.summary = "You must provide a summary");
  input.diets.length < 1
    ? (errors.diets = "Choose at least one diet")
    : (errors.diets = "");
  if (!input.image.includes("https://") && !input.image.includes("http://")) {
    errors.image = "This isn't a valid image address";
  } else {
    errors.image = "";
  }
  return errors;
}

export default function RecipeCreate() {
  const dispatch = useDispatch();
  //for updates:
  const {id} = useParams();
  const recipeToUpdate = useSelector((state) => state.allRecipes)
  const diets = useSelector((state) => state.diets);
  const [errors, setErrors] = useState({});

  const [updated, setUpdated] = useState(false)
  

  useEffect(() => {
    dispatch(getDiets());
    id &&  dispatch(getDetail(id))
  }, [dispatch, id]);

  const [input, setInput] = useState({
    title: "",
    summary: "",
    aggregateLikes: 0,
    healthScore: 0,
    analyzedInstructions: "",
    image: "",
    diets: [],
  });

  function handleChange(e) {
    setInput((input) => ({
      ...input,
      [e.target.name]: e.target.value,
    }));

    setErrors(
      validate({
        ...input,
        [e.target.name]: e.target.value,
      })
    );
  }

  function handleSelectDiet(e) {
   
    setErrors(
      validate({
        ...input,
        diets: [...input.diets, e.target.value],
      })
    );
    if(!input.diets.includes(e.target.value)){
    setInput((input) => ({
      ...input,
      diets: [...input.diets, e.target.value],
    }));
  }
  }


  function handleSubmit(e) {
    if (input.title && input.summary && input.image && input.diets.length > 0) {
      e.preventDefault();
      let createdOrUpdatedRecipe = input;
      createdOrUpdatedRecipe = {
        ...input,
        title: createdOrUpdatedRecipe.title,
        summary: createdOrUpdatedRecipe.summary,
        aggregateLikes: createdOrUpdatedRecipe.title,
        healthScore: createdOrUpdatedRecipe.healthScore,
        analyzedInstructions: createdOrUpdatedRecipe.analyzedInstructions,
        image: createdOrUpdatedRecipe.image,
        diets: createdOrUpdatedRecipe.diets
      }
      setErrors(validate(createdOrUpdatedRecipe));
      if(!Object.keys(errors).length && createdOrUpdatedRecipe.title){
        if(!id){
          dispatch(postRecipe(createdOrUpdatedRecipe),
          alert("Recipe succesfully Created!!"))
        } else {
          dispatch(updateRecipe(id,  createdOrUpdatedRecipe));
        }
      }
      
      
      setInput({
        title: "",
        summary: "",
        aggregateLikes: 0,
        healthScore: 0,
        analyzedInstructions: "",
        image: "",
        diets: [],
      });
      
    } else {
      e.preventDefault();
      alert("You must complete every field!!");
    }
  }

  function handleDelete(e, d) {
    e.preventDefault();
    setInput({
      ...input,
      diets: input.diets.filter((diet) => diet !== d),
    });
  }

  if(id && recipeToUpdate.title && !updated){
    setInput({
      ...input,
        title: recipeToUpdate.title,
        summary: recipeToUpdate.summary,
        aggregateLikes: recipeToUpdate.title,
        healthScore: recipeToUpdate.healthScore,
        analyzedInstructions: recipeToUpdate.analyzedInstructions,
        image: recipeToUpdate.image,
        diets: recipeToUpdate.diets
    });
    setUpdated(!updated)

  }

  return (
    <div className="create">
    
      <h1><img src="https://i.ibb.co/nmWgfS3/IMG-4878.png" alt="tiger eating a bone" border="0"/>Create your own Recipe here:</h1>
      <div className="form">
        <form onSubmit={(e) => handleSubmit(e)}>
          <div>
            <h2 className="form-title">Fill this form with your recipe details please </h2>
          </div>
          <div>
            <label>Plate Name:</label>
            <input
              className="inputCreate"
              placeholder="Complete here..."
              type="text"
              value={input.title}
              name="title"
              onChange={(e) => handleChange(e)}
            />
            {errors.title && <p>{errors.title}</p>}
          </div>
          <div>
            <label>Summary:</label>
            <input
              className="inputCreate"
              placeholder="Complete here..."
              type="text"
              value={input.summary}
              name="summary"
              onChange={(e) => handleChange(e)}
            />
            {errors.summary && <p>{errors.summary}</p>}
          </div>
          <div>
            <label>Score:</label>
            <input
              className="inputCreate"
              type="number"
              value={input.aggregateLikes}
              name="aggregateLikes"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <div>
            <label>Health Level:</label>
            <input
              className="inputCreate"
              type="number"
              value={input.healthScore}
              name="healthScore"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <div>
            <label className="labelInstr">Instructions:</label>
            <textarea
              type="text"
              className="instruction"
              placeholder="Complete here..."
              rows="5"
              value={input.analyzedInstructions}
              name="analyzedInstructions"
              onChange={(e) => handleChange(e)}
            />
          </div>
          <div>
            <label>Image:</label>
            <input
              className="inputCreate"
              type="text"
              placeholder="Example: https://..."
              value={input.image}
              name="image"
              onChange={(e) => handleChange(e)}
            />
            {errors.image && <p>{errors.image}</p>}
          </div>
          <div className="dietsCreate">
            <span>Type of Diet:</span>
            <select onChange={(e) => handleSelectDiet(e)}>
              {diets.map((d) => (
                <option value={d.name} key={d.name}>
                  {d.name}
                </option>
              ))}
            </select>
            {input.diets.map((d, i) => (
              <ul key={i}>
                <p>{d}</p>
                <button className="btnCreateD" onClick={(e) => handleDelete(e, d)}>x</button>
              </ul>
            ))}
            {errors.diets && <p>{errors.diets}</p>}
          </div>
          <button type="submit" className="btnCreate">
            Create Recipe
          </button>
          <Link to="/home">
        <button className="buttonToHome">Back to Home</button>
      </Link>
        </form>
      </div>
    </div>
  );
}
